export default function Hero() {
  const heroRef = useRef<HTMLDivElement>(null);
  const nameRef = useRef<HTMLHeadingElement>(null);
  const roleRef = useRef<HTMLParagraphElement>(null);
  const descRef = useRef<HTMLParagraphElement>(null);
  const btnRef = useRef<HTMLAnchorElement>(null);
  const marqueeRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      const nameChars = nameRef.current?.querySelectorAll(".char");
      if (nameChars) {
        gsap.set(nameChars, { opacity: 0, y: 80, rotateX: -90 });
        gsap.to(nameChars, {
          opacity: 1,
          y: 0,
          rotateX: 0,
          duration: 0.9,
          ease: "back.out(1.7)",
          stagger: 0.035,
          delay: 0.3,
        });
      }

      gsap.fromTo(
        roleRef.current,
        { opacity: 0, y: 30, letterSpacing: "0.5em" },
        {
          opacity: 1,
          y: 0,
          letterSpacing: "0.3em",
          duration: 1,
          ease: "power3.out",
          delay: 1.2,
        },
      );

      gsap.fromTo(
        descRef.current,
        { opacity: 0, y: 20 },
        { opacity: 1, y: 0, duration: 0.8, ease: "power3.out", delay: 1.6 },
      );

      gsap.fromTo(
        btnRef.current,
        { opacity: 0, scale: 0.9 },
        {
          opacity: 1,
          scale: 1,
          duration: 0.6,
          ease: "back.out(1.7)",
          delay: 2.0,
        },
      );

      gsap.fromTo(
        marqueeRef.current,
        { opacity: 0, y: 20 },
        { opacity: 1, y: 0, duration: 1, delay: 2.2 },
      );
    }, heroRef);

    return () => ctx.revert();
  }, []);

  const splitText = (text: string) =>
    text.split("").map((char, i) => (
      <span
        key={i}
        className="char inline-block"
        style={{ perspective: "600px" }}
      >
        {char === " " ? "\u00A0" : char}
      </span>
    ));

  const marqueeItems = [
    ...technologies,
    ...technologies,
    ...technologies,
    ...technologies,
  ];

  return (
    <section
      ref={heroRef}
      className="relative flex h-[calc(100vh-8rem)] flex-col items-center justify-center overflow-hidden px-6"
    >
      <h1
        ref={nameRef}
        className="hero-name text-center text-5xl font-bold tracking-tight text-black dark:text-white sm:text-6xl md:text-7xl lg:text-8xl"
      >
        {splitText("Carlos Martínez")}
      </h1>

      <p
        ref={roleRef}
        className="mt-6 text-lg font-light uppercase tracking-[0.3em] text-zinc-500 dark:text-zinc-400 sm:text-xl"
        style={{ opacity: 0 }}
      >
        Desarrollador web
      </p>

      <p
        ref={descRef}
        className="mt-4 max-w-lg text-center text-base text-zinc-600 dark:text-zinc-400 sm:text-lg"
        style={{ opacity: 0 }}
      >
        Creando experiencias web modernas, funcionales y con atención al
        detalle.
      </p>

      <a
        ref={btnRef}
        href="/cv.pdf"
        download
        className="mt-8 inline-flex items-center gap-2 rounded-full border border-zinc-300 bg-black px-6 py-3 text-sm font-medium text-white transition-all duration-300 hover:scale-105 hover:bg-zinc-800 dark:border-zinc-700 dark:bg-white dark:text-black dark:hover:bg-zinc-200"
        style={{ opacity: 0 }}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="16"
          height="16"
          viewBox="0 0 24 24"
          fill="none"
          stroke="currentColor"
          strokeWidth="2"
          strokeLinecap="round"
          strokeLinejoin="round"
        >
          <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4" />
          <polyline points="7 10 12 15 17 10" />
          <line x1="12" y1="15" x2="12" y2="3" />
        </svg>
        Descargar CV
      </a>

      <div
        ref={marqueeRef}
        className="absolute bottom-0 left-0 w-full overflow-hidden"
        style={{ opacity: 0 }}
      >
        <div className="marquee-bg py-4">
          <div className="marquee-track flex w-max items-center gap-10 sm:gap-14">
            {marqueeItems.map((tech, i) => (
              <div key={i} className="flex flex-shrink-0 items-center gap-2.5">
                <div
                  className="tech-icon h-6 w-6 sm:h-7 sm:w-7"
                  dangerouslySetInnerHTML={{ __html: tech.svg }}
                />
                <span
                  className="whitespace-nowrap text-xs font-semibold uppercase tracking-wider sm:text-sm"
                  style={{ color: tech.color }}
                >
                  {tech.name}
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>

      <style jsx>{`
        .hero-name {
          font-family: "Space Grotesk", sans-serif;
          cursor: default;
        }

        .marquee-bg {
          background: linear-gradient(
            180deg,
            transparent 0%,
            rgba(0, 0, 0, 0.03) 30%,
            rgba(0, 0, 0, 0.05) 100%
          );
          border-top: 1px solid rgba(0, 0, 0, 0.06);
        }

        :global(.dark) .marquee-bg {
          background: linear-gradient(
            180deg,
            transparent 0%,
            rgba(255, 255, 255, 0.02) 30%,
            rgba(255, 255, 255, 0.04) 100%
          );
          border-top: 1px solid rgba(255, 255, 255, 0.06);
        }

        .marquee-track {
          animation: marquee 35s linear infinite;
        }

        .marquee-track:hover {
          animation-play-state: paused;
        }

        @keyframes marquee {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }

        .tech-icon :global(svg) {
          width: 100%;
          height: 100%;
        }
      `}</style>
    </section>
  );
}
